frappe.ui.form.on('Salary Slip', {
	"employee": function(frm){
	// your code here
	    cur_frm.fields_dict['salary_structure_assignment'].get_query=function(frm) {
    	    return {
    		    filters: [['Salary Structure Assignment','employee','=',frm.employee]]
    	    }

        };
    }
}
)

//https://discuss.erpnext.com/t/is-it-possible-in-unsaved-form-to-use-get-query-method-with-filter-that-contain-one-of-forms-values/30153
