frappe.ui.form.on('Onsite Flow Measurement', {
	refresh(frm) {
		// your code here
		cur_frm.cscript.height=function(doc){
		    if (doc.biox_wier_notch_type=='Rectangular'){
		        doc.flow_volume =  1.85 * flt(doc.biox_crest_length)*(flt(doc.height)**1.5)  ;
		    } else if (doc.biox_wier_notch_type =='Triangular'){
		        doc.flow_volume = 1.42 * flt(doc.height)**2.5;
		    }
		  doc.flow_volume *=60*60*24/1000; // converts cubic meter per sec to MLD per Day
		  refresh_field('flow_volume');
		}
	}
})
